(function () {
  const warn = () => {
    throw new Error("Tesseract.js is not bundled yet. Replace popup/tesseract.min.js with the official build from npm (tesseract.js) or a local vendor copy.");
  };

  if (!window.Tesseract) {
    window.Tesseract = {
      recognize: async () => {
        warn();
      }
    };
  }
})();
